<style lang="scss">
  .title {
    color: rgb(var(--fourth));
    font-size: 28px;
    max-width: 500px;
    font-weight: bold;
    position: relative;

    &::before {
      content: " ";
      position: absolute;
      top: 10px;
      right: -40px;
      background: rgb(var(--second));
      width: 20px;
      height: 20px;
      border-radius: 6px;
    }
  }

  .description {
    color: rgb(var(--fourth));
    font-size: 16px;
    max-width: 500px;
    font-weight: 300;
    margin-top: 20px;
    white-space: pre-line;

    p {
      @apply text-right;
    }
  }

  .buttons {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-top: 30px;

    a {
      text-decoration: none;
      color: inherit;
      text-align: center;
    }

    div:nth-of-type(1) {
      margin-right: 0;
    }
  }

  .wrapper {
    position: relative;
  }

  .shape-bg svg path {
    fill: rgb(var(--second));
  }

  .dots {
    position: absolute;
    right: 190px;
    bottom: 108px;
  }

  .dots svg path {
    fill: rgb(var(--third));
  }

  .circle {
    position: absolute;
    right: -100px;
    bottom: 130px;
  }

  .circle svg line {
    stroke: rgb(var(--third));
  }

  .gif-section {
    position: absolute;
    display: flex;
    justify-content: center;
    align-items: center;
    background: white;
    width: 300px;
    height: 230px;
    border-radius: 25px;
    right: -58px;
    bottom: 65px;
  }

  .gif-section .gif-section-image {
    display: inline-block;
    width: 290px;
    height: 220px;
    border-radius: 20px;
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
  }

  @media (max-width: 991px) {
    .titlethree-texts {
      order: 1;
      align-items: center !important;
      margin-top: 100px;
    }

    .titlethree-shapes {
      justify-content: center !important;
      padding-right: 84px !important;
    }

    .shape-bg {
